Python class Solution: def removeDuplicates(self, nums: List[int]) -&gt; int: n = len(nums) if n &lt; 2: return n slow, fast = 1, 1 for fast in range(1,n): if nums[fast-1] != nums[fast]: nums[slow] = nums[fast] slow += 1 return slow Golang func removeDuplicates(nums []int) int { n := len(nums) if n &lt; 2 { return n } slow, fast := 1, 1 for fast &lt; n { if nums[fast-1] != nums[fast] { nums[slow] = nums[fast] slow++ } fast++ } return slow }

